{
  "openapi": "3.0.1",
  "info": {
    "title": "Student manager",
    "version": "v1"
  },
  "paths": {
    "/api/Student/GetAll": {
      "get": {
        "tags": [
          "Student"
        ],
        "summary": "Get all students",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/StudentsListVm"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/StudentsListVm"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/StudentsListVm"
                }
              }
            }
          }
        }
      }
    },
    "/api/Student/GetAllFiltered": {
      "post": {
        "tags": [
          "Student"
        ],
        "summary": "Get all students with filter parameter",
        "requestBody": {
          "description": "Filter parameters",
          "content": {
            "application/json-patch+json": {
              "schema": {
                "$ref": "#/components/schemas/GetStudentsListQuery"
              }
            },
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/GetStudentsListQuery"
              }
            },
            "text/json": {
              "schema": {
                "$ref": "#/components/schemas/GetStudentsListQuery"
              }
            },
            "application/*+json": {
              "schema": {
                "$ref": "#/components/schemas/GetStudentsListQuery"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/StudentsListVm"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/StudentsListVm"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/StudentsListVm"
                }
              }
            }
          }
        }
      }
    },
    "/api/Student/Get/{id}": {
      "get": {
        "tags": [
          "Student"
        ],
        "summary": "Get student details",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "Student ID",
            "required": true,
            "schema": {
              "type": "string",
              "description": "Student ID",
              "format": "uuid"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/StudentDetailVm"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/StudentDetailVm"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/StudentDetailVm"
                }
              }
            }
          },
          "404": {
            "description": "Not Found",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      }
    },
    "/api/Student/Update": {
      "put": {
        "tags": [
          "Student"
        ],
        "summary": "Update student",
        "requestBody": {
          "description": "New student details",
          "content": {
            "application/json-patch+json": {
              "schema": {
                "$ref": "#/components/schemas/UpdateStudentCommand"
              }
            },
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/UpdateStudentCommand"
              }
            },
            "text/json": {
              "schema": {
                "$ref": "#/components/schemas/UpdateStudentCommand"
              }
            },
            "application/*+json": {
              "schema": {
                "$ref": "#/components/schemas/UpdateStudentCommand"
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Success"
          },
          "404": {
            "description": "Not Found",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      }
    },
    "/api/Student/Create": {
      "post": {
        "tags": [
          "Student"
        ],
        "summary": "Create new student",
        "requestBody": {
          "description": "New student details",
          "content": {
            "application/json-patch+json": {
              "schema": {
                "$ref": "#/components/schemas/CreateStudentCommand"
              }
            },
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/CreateStudentCommand"
              }
            },
            "text/json": {
              "schema": {
                "$ref": "#/components/schemas/CreateStudentCommand"
              }
            },
            "application/*+json": {
              "schema": {
                "$ref": "#/components/schemas/CreateStudentCommand"
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Success"
          }
        }
      }
    },
    "/api/Student/Delete/{id}": {
      "delete": {
        "tags": [
          "Student"
        ],
        "summary": "Delete student",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "Student ID",
            "required": true,
            "schema": {
              "type": "string",
              "description": "Student ID",
              "format": "uuid"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Success"
          },
          "404": {
            "description": "Not Found",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      }
    },
    "/api/Student/CheckNicknameNotTaken": {
      "post": {
        "tags": [
          "Student"
        ],
        "summary": "Check if nickname is already taken",
        "requestBody": {
          "content": {
            "application/json-patch+json": {
              "schema": {
                "$ref": "#/components/schemas/CheckNicknameNotTakenQuery"
              }
            },
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/CheckNicknameNotTakenQuery"
              }
            },
            "text/json": {
              "schema": {
                "$ref": "#/components/schemas/CheckNicknameNotTakenQuery"
              }
            },
            "application/*+json": {
              "schema": {
                "$ref": "#/components/schemas/CheckNicknameNotTakenQuery"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/NicknameNotTakenVm"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/NicknameNotTakenVm"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/NicknameNotTakenVm"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Gender": {
        "enum": [
          0,
          1
        ],
        "type": "integer",
        "format": "int32"
      },
      "StudentVm": {
        "type": "object",
        "properties": {
          "Id": {
            "type": "string",
            "format": "uuid"
          },
          "Name": {
            "type": "string",
            "nullable": true
          },
          "Surname": {
            "type": "string",
            "nullable": true
          },
          "Patronymic": {
            "type": "string",
            "nullable": true
          },
          "Gender": {
            "$ref": "#/components/schemas/Gender"
          },
          "Nickname": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "StudentsListVm": {
        "type": "object",
        "properties": {
          "TotalCount": {
            "type": "integer",
            "format": "int32"
          },
          "Students": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/StudentVm"
            },
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "GetStudentsListQuery": {
        "type": "object",
        "properties": {
          "SearchString": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "StudentDetailVm": {
        "type": "object",
        "properties": {
          "Id": {
            "type": "string",
            "format": "uuid"
          },
          "Name": {
            "type": "string",
            "nullable": true
          },
          "Surname": {
            "type": "string",
            "nullable": true
          },
          "Patronymic": {
            "type": "string",
            "nullable": true
          },
          "Gender": {
            "$ref": "#/components/schemas/Gender"
          },
          "Nickname": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "ProblemDetails": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "nullable": true
          },
          "title": {
            "type": "string",
            "nullable": true
          },
          "status": {
            "type": "integer",
            "format": "int32",
            "nullable": true
          },
          "detail": {
            "type": "string",
            "nullable": true
          },
          "instance": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": {
          "type": "object",
          "additionalProperties": false
        }
      },
      "UpdateStudentCommand": {
        "type": "object",
        "properties": {
          "Id": {
            "type": "string",
            "format": "uuid"
          },
          "Name": {
            "type": "string",
            "nullable": true
          },
          "Surname": {
            "type": "string",
            "nullable": true
          },
          "Patronymic": {
            "type": "string",
            "nullable": true
          },
          "Gender": {
            "$ref": "#/components/schemas/Gender"
          },
          "Nickname": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "CreateStudentCommand": {
        "type": "object",
        "properties": {
          "Id": {
            "type": "string",
            "format": "uuid"
          },
          "Name": {
            "type": "string",
            "nullable": true
          },
          "Surname": {
            "type": "string",
            "nullable": true
          },
          "Patronymic": {
            "type": "string",
            "nullable": true
          },
          "Gender": {
            "$ref": "#/components/schemas/Gender"
          },
          "Nickname": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "CheckNicknameNotTakenQuery": {
        "type": "object",
        "properties": {
          "StudentId": {
            "type": "string",
            "format": "uuid"
          },
          "Nickname": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "NicknameNotTakenVm": {
        "type": "object",
        "properties": {
          "Result": {
            "type": "boolean"
          }
        },
        "additionalProperties": false
      }
    }
  }
}
